import React, { useState, useEffect } from "react";
import axios from "axios";

const DisplayData = () => {
  const [landingData, setLandingData] = useState([]);
  const [apacheData, setApacheData] = useState([]);
  const [activeTab, setActiveTab] = useState("landing");

  useEffect(() => {
    const fetchData = async (apiEndpoint) => {
      try {
        const response = await axios.get(
          `http://localhost/your_project_folder/process_data.php?api=${apiEndpoint}`
        );
        if (apiEndpoint === "wp_s3cu_form_on_landing") {
          setLandingData(response.data);
        } else {
          setApacheData(response.data);
        }
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData(activeTab === "landing" ? "wp_s3cu_form_on_landing" : "apache-logs");
  }, [activeTab]);

  const switchTab = (tab) => {
    setActiveTab(tab);
  };

  return (
    <div>
      <div>
        <button onClick={() => switchTab("landing")}>WP_s3cu_form_on_landing</button>
        <button onClick={() => switchTab("apache_logs")}>Apache Logs</button>
      </div>
      <table>
        <thead>
          {activeTab === "landing" ? (
            <tr>
              {/*Define the column headers for the 'landing' table*/}
            </tr>
          ) : (
            <tr>
              {/*Define the column headers for the 'apache_logs' table */}
            </tr>
          )}
        </thead>
        <tbody>
          {activeTab === "landing"
            ? landingData.map((item) => (
                <tr key={item.id}>
                  {/* Render the rows for the 'landing' table */}
                </tr>
              ))
            : apacheData.map((item) => (
                <tr key={item.id}>
                  {/* Render the rows for the 'apache_logs' table */}
                </tr>
              ))}
        </tbody>
      </table>
    </div>
  );
};

export default DisplayData;
