import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './TableStyle.css';

const fetchDataOnPageChange = async (activeLog, page, setData) => {
  try {
    const response = await axios.get(`https://localhost/backend/process_data.php?api=${activeLog}&page=${page}`);
    setData(page === 1 ? response.data : (prevData) => [...prevData, response.data]);
  } catch (error) {
    console.error("Error fetching data: ", error);
  }
};

const DisplayData = () => {
  const [data, setData] = useState([]);
  const [activeLog, setActiveLog] = useState('wp_s3cu');
  const [page, setPage] = useState(1);

  const pageInc = () => setPage(prevPage => prevPage + 1);

  // вызываем при изменении журнала
  useEffect(() => {
    fetchDataOnPageChange(activeLog, 1, setData);
  }, [activeLog]);

  // вызываем при изменении страницы
  useEffect(() => {
    fetchDataOnPageChange(activeLog, page, setData);
  }, [page]);

  const renderHeaders = () => {
    return activeLog === "wp_s3cu" ? (
      <thead>
        <tr>
          <th>ID</th>
          <th>Main Point</th>
          <th>Sex</th>
          <th>Age</th>
          <th>Params</th>
          <th>Created At</th>
          <th>Updated At</th>
        </tr>
      </thead>
    ) : (
      <thead>
        <tr>
          <th>ID</th>
          <th>IP</th>
          <th>DateTime</th>
          <th>Request Method</th>
          <th>URL</th>
          <th>Protocol</th>
          <th>Status Code</th>
          <th>Response Size</th>
          <th>Referrer</th>
          <th>User Agent</th>
        </tr>
      </thead>
    );
  };

  return (
    <div>
      <button onClick={() => {setActiveLog("wp_s3cu"); setPage(1);}}>
        Show WP_s3cu
      </button>
      <button onClick={() => {setActiveLog("apache_logs"); setPage(1);}}>
        Show Apache Logs
      </button>
      <table className="styled-table">
        {renderHeaders()}
        <tbody>
          {data.map((item, index) => (
            <tr key={index}>
              {Object.values(item).map((value, i) => (
                <td key={i}>{value}</td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
      <button onClick={pageInc}>
        Load More
      </button>
    </div>
  );
};

export default DisplayData;
