import React, { useState, useEffect } from 'react';
import axios from 'axios';

const DisplayData = () => {
  const [data, setData] = useState([]);
  const [activeLog, setActiveLog] = useState('wp_s3cu_form_on_landing');
  const [page, setPage] = useState(1);

  const fetchNextPage = () => {
    setPage((prevPage) => prevPage + 1);
  }

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(`https://localhost/backend/process_data.php?api=${activeLog}&page=${page}`);
        setData((prevData) => [...prevData, ...Object.values(response.data)]);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();
  }, [activeLog, page]);

return (
  <div>
    <button onClick={() => setActiveLog("wp_s3cu_form_on_landing")}>
      Show WP_s3cu_form_on_landing
    </button>
    <button onClick={() => setActiveLog("apache_logs")}>
      Show Apache Logs
    </button>
    <table>
      <tbody>
        {data.map((item, index) => (
          <tr key={index}>
              {Object.values(item).map((value, i) => <td key={i}>{value}</td>)}
          </tr>
        ))}
      </tbody>
    </table>
    <button onClick={fetchNextPage}>
      Load More
    </button>
  </div>
  );

};

export default DisplayData;
