import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './TableStyle.css';

const DisplayData = () => {
  const [data, setData] = useState([]);
  const [activeLog, setActiveLog] = useState('wp_s3cu_form_on_landing');
  const [page, setPage] = useState(1);

  const fetchNextPage = () => {
    setPage(page + 1);
  }

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(`https://localhost/backend/process_data.php?api=${activeLog}&page=${page}`);

        if(page === 1){
          setData(Object.values(response.data));
        }
        else {
          setData((prevData) => [...prevData, ...Object.values(response.data)]);
        }

      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();
  }, [activeLog, page]);


  
  const renderHeaders = () => {
    return activeLog === "wp_s3cu_form_on_landing" ? (
      <thead>
        <tr>
          <th>ID</th>
          <th>Main Point</th>
          <th>Sex</th>
          <th>Age</th>
          <th>Params</th>
          <th>Created At</th>
          <th>Updated At</th>
        </tr>
      </thead>
    ) : (
      <thead>
        <tr>
          <th>ID</th>
          <th>IP</th>
          <th>DateTime</th>
          <th>Request Method</th>
          <th>URL</th>
          <th>Protocol</th>
          <th>Status Code</th>
          <th>Response Size</th>
          <th>Referrer</th>
          <th>User Agent</th>
        </tr>
      </thead>
    );
  };

  return (
    <div>
      <button onClick={() => {setActiveLog("wp_s3cu_form_on_landing"); setPage(1);}}>
        Show WP_s3cu_form_on_landing
      </button>
      <button onClick={() => {setActiveLog("apache_logs"); setPage(1);}}>
        Show Apache Logs
      </button>
      <table className="styled-table">
        {renderHeaders()}
        <tbody>
          {data.map((item, index) => (
            <tr key={index}>
              {Object.values(item).map((value, i) => (
                <td key={i}>{value}</td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
      <button onClick={fetchNextPage}>
        Load More
      </button>
    </div>
  );
};

export default DisplayData;
