import React, { useState, useEffect } from 'react'; // Импорт необходимых библиотек и хуков
import axios from 'axios'; // Импорт библиотеки axios для выполнения HTTP-запросов
import './TableStyle.css'; // Импорт CSS-стилей для таблицы

const DisplayData = () => {
  const [data, setData] = useState([]); // Хранения данных
  const [activeLog, setActiveLog] = useState('wp_s3cu_form_on_landing'); // Хранения текущей таблицы
  const [page, setPage] = useState(1); // Хранение текущей страницы

  const fetchNextPage = () => { // Функция для загрузки следующей странцицы
    setPage(prevPage => Number(prevPage) + 1);
  }

  // Запрос данных при первой загрузке сайта или же если меняется activeLog
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(`https://localhost/backend/process_data.php?api=${activeLog}&page=${page}`);
        if (page > 1) {
          setData((prevData) => [...prevData, ...Object.values(response.data)]);
        }
      } catch (error) {
        console.error("Error fetching data: ", error);
      }
    };
  
    if (page > 1) {
      fetchData();
    }
  }, [activeLog, page]);
  

  // Отрисовка заголовков в зависимости от текущей таблицы
  const renderHeaders = () => {
    return activeLog === "wp_s3cu_form_on_landing" ? (
      <thead>
        <tr>
          <th>ID</th>
          <th>Main Point</th>
          <th>Sex</th>
          <th>Age</th>
          <th>Params</th>
          <th>Created At</th>
          <th>Updated At</th>
        </tr>
      </thead>
    ) : (
      <thead>
        <tr>
          <th>ID</th>
          <th>IP</th>
          <th>DateTime</th>
          <th>Request Method</th>
          <th>URL</th>
          <th>Protocol</th>
          <th>Status Code</th>
          <th>Response Size</th>
          <th>Referrer</th>
          <th>User Agent</th>
        </tr>
      </thead>
    );
  };

  // Заполнение таблицы при нажатии на кнопку
  return (
    <div>
      <button onClick={() => {setActiveLog("wp_s3cu_form_on_landing"); setPage(1); setData([]);}}>
        Show WP_s3cu_form_on_landing
      </button>
      <button onClick={() => {setActiveLog("apache_logs"); setPage(1); setData([]);}}>
        Show Apache Logs
      </button>
      <table className="styled-table">
        {renderHeaders()}
        <tbody>
          {data.map((item, index) => (
            <tr key={index}>
              {Object.values(item).map((value, i) => (
                <td key={i}>{value}</td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
      <button onClick={fetchNextPage}>
        Load More
      </button>
    </div>
  );
};

export default DisplayData;
